void render_layers(void) {
    bool adjust = layer_state_is(_ADJUST);
    bool lower = layer_state_is(_LOWER) & !adjust;
    bool raise = layer_state_is(_RAISE) & !adjust;

    if(lower){
        _render_layer2_logo();
    } else if(raise){
        _render_layer3_logo();
    } else if(adjust){
        _render_layer4_logo();
    } else {
        _render_layer1_logo();
    }
}

oled_rotation_t oled_init_user(oled_rotation_t rotation) {
    if (!is_keyboard_master()) {
      return OLED_ROTATION_0;
    }
    return rotation;
}

// Used to draw on to the oled screen
bool oled_task_user(void) {
    if (is_keyboard_master()) {
        render_anim(); // renders pixelart

        oled_set_cursor(0, 0);                           // sets cursor to (row, column) using charactar spacing (5 rows on 128x32 screen, anything more will overflow back to the top)
        sprintf(wpm_str, "%03d WPM", get_current_wpm()); // edit the string to change wwhat shows up, edit %03d to change how many digits show up
        oled_write(wpm_str, false);                      // writes wpm on top left corner of string
    } else {
        render_layers();
    }

    return false;
}
